<style>
    body{
        background-color: #333333;
    }
    .main{
        width: 300px;
        height: 500px;
        background-color: rgba(255,255,255,0.6);
        margin: 10px auto;
        border-radius: 10px;
        padding: 10px;
        position: relative;
    }
    .bolita{
        border-radius: 50%;
        background-color: rgba(255,0,0,0.6);
        width: 50px;
        height: 50px;
        position: absolute;
    }
    .uno{
        top: 10px;
        right: 20px;
    }
    .dos{
        top: 10px;
        left: 0px;
    }
</style>
</head>
<body>
<header></header>
<main>
    <div class="main">
      <h1>TEST</h1>
        <div class="bolita uno"></div>
        <div class="bolita dos"></div>
    </div>
</main>
<footer></footer>
